mapper.select:
SELECT
	id,
	image,
	NAME,
	caption,
	is_custom,
	price,
	sales
FROM
	dm_goods
WHERE
	version = 1

	AND gender = #{gender}


	AND product_scene = #{scene}


	AND product_category = #{category}


	AND product_season= #{season}


	AND price >= #{startPrice}


	AND price <= #{endPrice}

SELECT id, size FROM dm_size WHERE id = ANY ( SELECT s.id FROM dm_product p LEFT JOIN dm_size s ON s.id = p.size WHERE p.goods = #{goods}
AND p.material = #{material}
AND p.size = #{size}
)

SELECT DISTINCT m.id, m.material, p.price, p.stock FROM dm_product p LEFT JOIN dm_material m ON m.id = p.material WHERE p.goods = #{goods}
AND p.size = #{size}
AND p.material = #{material}
